const startingMinutes=1;let time=60*startingMinutes;time--;const counterEl=document.getElementById("counter"),msg=document.getElementById("secret-msg"),explosion=document.getElementById("explo");var timer=setInterval(updateCounter,1e3);function updateCounter(){var e=Math.floor(time/60);let t=time%60;t=t<10?"0"+t:t,time--,counterEl.innerHTML=`<b>[This message will self destruct in ${e}:${t}.]</b>`,-1==time&&(counterEl.innerHTML="",clearInterval(timer),explosion.style.display="block",explosion.classList.add("active"),msg.style.display="none")}